var minOperations = function (boxes) {
    let answer = new Array(boxes.length);
    answer.fill(0);
    for (answerIndex in boxes) {
        for ([index, emptyOrNot] of Object.entries(boxes)) {
            if (emptyOrNot==="1") {
                answer[answerIndex] = answer[answerIndex] + Math.abs(answerIndex - index);
            }
        }
    }
    return answer;
}
